import React, { Component } from 'react'
import EmailItem from './EmailItem'

const EmailTree = ({date,recipient,subject}) => {
    return (    
      <div>
        <h1 classname="compHeading">Email List</h1>
        {this.state.emailList.map(email =>{
          <EmailItem date={date} recipient={recipient} subject={subject} />
        })}
      </div>
    )

}

export default EmailTree
